import React, { useState, useEffect } from 'react'; 
import { Header, Input, Button, List } from 'semantic-ui-react';

const endpoint = "http://localhost:9000"; 

function TodoList() {
  const [task, setTask] = useState(""); // Hook for task input
  const [items, setItems] = useState([]); // Ensure items is initialized as an array
  const [loading, setLoading] = useState(false); // Loading state for task creation
  const [error, setError] = useState(null); // Error state

  useEffect(() => {
    getTasks(); // Fetch tasks when the component mounts
  }, []);
  
  // Handles input changes
  const onChange = (e) => {
    setTask(e.target.value);
  };

  // Handles form submission
  const onSubmit = async (e) => {
    e.preventDefault();
    if (task.trim()) {
      try {
        setLoading(true); // Start loading
        const response = await fetch(`${endpoint}/api/tasks`, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ task }),
        });
        if (response.ok) {
          await getTasks(); // Fetch updated tasks after submitting
          setTask(""); // Clear input after submission
        } else {
          throw new Error("Failed to create task");
        }
      } catch (error) {
        console.error("Error creating task:", error);
        setError("Error creating task"); // Set error message
      } finally {
        setLoading(false); // Stop loading
      }
    }
  };

  // Fetches existing tasks from the backend
  const getTasks = async () => {
    try {
      const response = await fetch(`${endpoint}/api/tasks`); // Note the plural 'tasks'
      if (!response.ok) throw new Error("Network response was not ok");
      const data = await response.json();
      setItems(data);
    } catch (error) {
      console.error("Error fetching tasks:", error);
      setError("Error fetching tasks"); // Set error message
    }
  };

  // Handles task undo
  const undoTask = async (id) => {
    try {
      setLoading(true); // Start loading
      const response = await fetch(`${endpoint}/api/undoTask/${id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
      });
      if (response.ok) {
        await getTasks(); // Refresh tasks after undoing
      } else {
        throw new Error("Failed to undo task");
      }
    } catch (error) {
      console.error("Error undoing task:", error);
    } finally {
      setLoading(false); // Stop loading
    }
  };

  // Handles task deletion
  const deleteTask = async (id) => {
    try {
      setLoading(true); // Start loading
      const response = await fetch(`${endpoint}/api/deleteTask/${id}`, {
        method: "DELETE",
      });
      if (response.ok) {
        await getTasks(); // Refresh tasks after deletion
      } else {
        throw new Error("Failed to delete task");
      }
    } catch (error) {
      console.error("Error deleting task:", error);
    } finally {
      setLoading(false); // Stop loading
    }
  };

  // Handles deletion of all tasks
  const deleteAllTasks = async () => {
    try {
      setLoading(true); // Start loading
      const response = await fetch(`${endpoint}/api/deleteAllTasks`, {
        method: "DELETE",
      });
      if (response.ok) {
        await getTasks(); // Refresh tasks after deletion
      } else {
        throw new Error("Failed to delete all tasks");
      }
    } catch (error) {
      console.error("Error deleting all tasks:", error);
    } finally {
      setLoading(false); // Stop loading
    }
  };

  return (
    <div className="row">
      <Header as="h2" color="yellow">ToDoList</Header>

      <div className="row">
        <form onSubmit={onSubmit}>
          <Input
            type="text"
            name="task"
            onChange={onChange}
            value={task}
            fluid
            placeholder="Create task"
          />
          <Button type="submit" color="yellow" disabled={loading}>
            {loading ? "Adding Task..." : "Add Task"} {/* Submit button */}
          </Button>
        </form>
      </div>

      {error && <p style={{ color: "red" }}>{error}</p>} {/* Display error if any */}

      <List divided relaxed>
        {items.length === 0 && !loading && <p>No tasks available</p>}
        {items.map((item) => (
          <List.Item key={item._id}> {/* Assuming each item has a unique '_id' */}
            <List.Content>
              <List.Header>{item.task}</List.Header> {/* Render task name */}
              <Button onClick={() => undoTask(item._id)} color="green" disabled={loading}>Undo</Button>
              <Button onClick={() => deleteTask(item._id)} color="red" disabled={loading}>Delete</Button>
            </List.Content>
          </List.Item>
        ))}
      </List>

      <Button onClick={deleteAllTasks} color="red" disabled={loading}>Delete All Tasks</Button> {/* Delete all tasks button */}
    </div>
  );
}

export default TodoList;
