import React, { useState, useEffect } from 'react'; 
import { Header, Input } from 'semantic-ui-react';

let endpoint="http://localhost:9000";
function TodoList() {
  const [task, setTask] = useState(""); // Hook for task input
  const [items, setItems] = useState([]); // Hook for storing tasks

  useEffect(() => {
    getTask();
  }, []);

  // Handles input changes
  const onChange = (e) => {
    setTask(e.target.value);
  };

  // Handles form submission
  const onSubmit = async (e) => {
    e.preventDefault();
    if (task.trim()) {
      try {
        const response = await fetch(`${endpoint}/api/task`, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ task }),
        });
        if (response.ok) {
          setItems([...items, task]); // Add task to items
          setTask(""); // Clear input
        }
      } catch (error) {
        console.error("Error creating task:", error);
      }
    }
  };
  
  // Fetches existing tasks (placeholder function for now)
  const getTask = async () => {
    try {
      const response = await fetch(`${endpoint}/api/task`);
      const data = await response.json();
      setItems(data);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };
 

  

  return (
    <div className="row">
      <Header as="h2" color="yellow">ToDoList</Header>

      <div className="row">
        <form onSubmit={onSubmit}>
          <Input
            type="text"
            name="task"
            onChange={onChange}
            value={task}
            fluid
            placeholder="Create task"
          />
        </form>
      </div>

      <ul>
        {items.map((item, index) => (
          <li key={index}>{item}</li>
        ))}
      </ul>
    </div>
  );
}

export default TodoList;
